import styled, { createGlobalStyle } from "styled-components";

export default createGlobalStyle`
*{
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

li{
  list-style: none;
}

body{
  background-color: ${({theme}) => theme.body};
  color: ${({theme}) => theme.text}; 
}


`;

export const Container = styled.div`
  max-width: 600px;
  margin: 120px auto;
  padding-right: 20px;
  padding-left: 10px;

`


