import React from 'react'
import { useNavigate } from 'react-router-dom'
import Indicater from '../Indicater'
import styled from 'styled-components'

const H_section = ({ status }) => {
    const move = (section) => {
        navigate('/menu/'+section)
    }
    const navigate = useNavigate()

  return (
    <div>
        <Section key={ status.section } onClick={() => navigate('/menu/'+status.section)}>
            <div to={'/menu'}>
                <div >Section{ status.section } : 特に分類なし</div>
                <div className="section-bars">
                    <p>進捗</p>
                    <p className="prg-percent">{ status.progress }%</p>
                    {/* <div className="bar-wrap bar-se1-wrap light-theme">
                        <div className="bar bar-se1 light-theme"></div>
                    </div> */}
                    <Indicater bar_color='blue' percentage={status.progress}/>
                    <p>暗記</p>
                    <p className="ar-percent">{ status.answerrate }%</p>
                    <Indicater bar_color='green' percentage={status.answerrate}/>
                    {/* <div className="bar-wrap bar-se2-wrap light-theme">
                        <div className="bar bar-se2 light-theme"></div>
                    </div> */}
                </div>
            </div>
            
        </Section>
     
    </div>
  )
}

const Section = styled.li`
width: 100%;
border: solid 2px black;
border-radius: 5px;
margin-bottom: 30px;
box-shadow: rgb(255, 216, 109) 6px 6px, black 6px 6px 0 2px;
border-radius: 15px;
padding: ;
`

export default H_section
