import React, { useEffect } from 'react';
import axios from 'axios';
import { Link, useParams, useNavigate } from 'react-router-dom';
import './Section_menu.css'
import Statusbox from './Statusbox'
import styled from 'styled-components';

function Menu({ status, setStatus }) {
    let params = useParams();
    const section = params.sectionNum;
    const updateStatus = () => {
        axios.get(`http://localhost:3000/api/tango/result/${section}`)
        .then(response => setStatus(response.data))
    }
    useEffect(() => {
        updateStatus()
      }, [])

    const navigate = useNavigate()
    const move = () => {
        navigate('/quez/'+section)
    }
    const startOver = () => {
        axios.post(`http://localhost:3000/api/tango/clear/${section}/0/3`)
        navigate('/quez/'+section)
    }



  return (

    <div>
        <div className="start">
            <Link className="start-hide" to={'/'}>←Home</Link>
            <h2 className="section-num">Section{section}</h2>
        </div>
        <div>
            <Statusbox
                text='進捗'
                percentage={status ? `${status.answered}` : '0' }
                bar_color='blue'
                emp='3'
            />
            <Statusbox
                text='定着度'
                percentage={status ? `${status.answerrate}` : '0' }
                bar_color='green'
                emp='3'
            />
        </div>


        <div className="start-btn box light-theme">
            <div onClick={()=>move()}>始める</div> 
        </div>
        <div className="mistake-btn box light-theme">
            <div onClick={() => startOver()}>1からはじめる</div> 
        </div>
        <div className="new-btn box light-theme">
            <a href="/menu/3">新しい</a> 
        </div>
        <Movemenu>
            <div onClick={updateStatus}>Before</div>
            <Link to='/menu/'>Next</Link>
        </Movemenu>
    </div>
  )
}

const Movemenu = styled.div``

export default Menu
