const Homestatus = ({recent}) => {
    const u = 24
  return (
    <div>
        <div className="status-wrap light-theme"> 
            <h2>Status</h2>
            <ul className="status-box-wrap">
                <S>
                    <Statusbox 
                        section={recent.section}
                        text='進捗'
                        bar_color='#1b91ff'
                        percentage={recent.answered}
                        emp='2'
                    />
                </S>
                <S>
                    <Statusbox 
                        text='正解率'
                        section={recent.section}
                        bar_color='#70ba08'
                        percentage={recent.answerrate}
                        emp='2'
                    />
                </S>
                <li className="status box light-theme"></li>
            </ul>
            <div className="section-title1">
                <h2>Section</h2>
            </div>    
        </div>

    </div>
  )
}

const S = styled.div`
    width: 180px;
    margin-right: 20px;
    margin-bottom: 13px;
    border-radius: 15px;
    background-color: $;
    &:hover{
        background-color: ${({theme})=>theme.hover};
    }
`

export default Homestatus
