import { useState, useEffect } from 'react'
import { Link } from 'react-router-dom'
import Homestatus from './Homestatus'
import Section from './Section'
import './Home.css'
import axios from 'axios'


function Home({ setStatus }) {
  const [statuses, setStatuses] = useState([
    {section:'  Loading...', answered:'0', answerrate:'0'},
    {section:'  Loading...', answered:'0', answerrate:'0'},
    {section:'  Loading...', answered:'0', answerrate:'0'},
    {section:'  Loading...', answered:'0', answerrate:'0'},
    {section:'  Loading...', answered:'0', answerrate:'0'},
    {section:'  Loading...', answered:'0', answerrate:'0'},
  ])
  const [recent, setRecent] = useState({
    section:" ",
    answered:"0",
    answerrate:"0"
  })

  useEffect(() => {
    axios.get('http://localhost:3000/api/tango/status')
      .then(response => {
        setStatuses(response.data)
      })
    axios.get('http://localhost:3000/api/tango/mark')
      .then(response => {
        setRecent(response.data.recent)
      })
    },[])

  return (
    <div>
        <Homestatus recent={recent}/>
        {statuses.map((status, index) => (
            <Section status={status} index={index} key={index} setStatus={setStatus}/>
        ))}
        <Link to='/menu'>menu</Link>
    </div>
  )
}

export default Home
