  ]) 

  useEffect(() => {
    const f = async () => {
      if(!status){
        const res = await axios.get(`http://localhost:3000/api/tango/status1/${section}`)   
        if(res.data.answered!=100){
          axios.get(`http://localhost:3000/api/tango/getquez/${section}/3`)
            .then(response => setWords(response.data))
          console.log("1")
        }else{
          axios.get(`http://localhost:3000/api/tango/getquez/${section}/0`)
            .then(response => setWords(response.data))
          console.log("2")
        }
        console.log("succses")

      }
      // if(status.answered==="100"){
