      //     .then(response => setWords(response.data))

      // }else{
      // }
    }
    f()
  },[])

  const addAnswer = (id, learning) => {
    axios.post(`http://localhost:3000/api/tango/answer`,{
      id,
      learning,
    })
  }

  var total = 12
  var tm = 8000;
  var fn = function(){
    setVisible(true)
  };
  var timeout = setTimeout(fn, tm);

  // const [resultNum, setResult] = useState()
