import React, { useState } from 'react'
import styled from 'styled-components'

const Quez = () => {
  const [answernum, setNum] = useState(0)
  const [visible, setVisible] = useState(0)
  const [words, setWords] = useState([
    {korean:'의사', meaning:'医者', pronounce:'ウィサ', learning:0, section:1},
    {korean:'위치', meaning:'位置', pronounce:'ウィチ', learning:0, section:1},
    {korean:'사랑', meaning:'愛', pronounce:'サラン', learning:0, section:1}
  ]) 
  const num = () => {
    setNum(answernum+1)
    console.log(answernum)
  }

  return (
    <div>
      {words.map((word, index) => (
      <Card key={index} answernum={answernum} num={index}>
        <Section_num>Section{word.section}</Section_num>
        <Progress_bar><Bar>({index}+1)</Bar></Progress_bar>
        <Word><p>{word.korean}</p></Word>
        <Card_hidden>
          <P1>{word.meaning}</P1>
          <P2>{word.pronounce}</P2>
        </Card_hidden>
        <Button_wrap>
          <Button onClick={num} color='#87d3ff'>〇</Button>
          <Button color='#ffe100'  >△</Button>
          <Button color='#ff9a8f'>✕</Button>
        </Button_wrap>

        <Showanswer><Show>答えを見る</Show></Showanswer>
      </Card>
      ))}

    </div>
  )
}

export default Quez

const Card = styled.div`
  position: fixed;
  background-color: white;
  /* position: fixed;
  top: 110px;
  bottom: 0;
  left: 0;
  right: 0;
  margin: auto; */
  /* max-width: 400px;
  height: 500px;
  text-align: center;
  border: solid 3px rgb(32, 32, 32);
  border-radius: 15px;
  z-index: 50;
  background-color: white;
  display: none;
  box-shadow: 5px 5px rgb(151, 208, 255); */
  width: 400px;
  height: 500px;
  text-align: center;
  border: 2px solid black;
  border-radius: 15px;
  display: ${(props) => props.num===props.answernum ? 'block' : 'none'};
`

const Section_num = styled.div`
  font-size: 20px;
  text-align: start;
  margin-left: 20px;
  margin-top: 5px;
`

const Progress_bar = styled.div`
  width: 90%;
  margin: auto;
  height: 20px;
  background-color: #FFC3C3;
  border-radius: 10px;
`

const Bar = styled.div`
  background-color: #FF7171;
  height: 20px;
  width: 50%;
  overflow: hidden;
  border-radius: 10px;
`   

const Word = styled.div`
  background-color: #FFEAC2;
  display: inline-block;
  border-radius: 20px;
  padding: 0 10px;
  margin-top: 10px;
  font-size: 64px;
  padding: 0 15px;
  margin: 23px;
`

const Card_hidden = styled.div`

`
const P1 = styled.p`
  font-size: 32px;
`

const P2 = styled.p`
  font-size: 13px;
`

const Button_wrap = styled.div`
  width: 100%;
  display: flex;
  height: 130px;
  position: absolute;
  top: 280px;

`

const Button = styled.button`
  width: 33%;
  border: solid 3px ${(props) => props.color};
  border-radius: 24px;
  margin: 0 5px;
  text-align: center;
  font-size: 50px;
  background-color: white;
` 



const Showanswer = styled.div`
  position: absolute;
  top: 425px;
  width: 100%;
`

const Show = styled.button`  
  width: 150px;
  height: 50px;
  border: 2px solid black;
  border-radius: 15px;
  display: inline-block;
  background-color: #CBECFF;
  font-size: 20px;
`
