import React, { useEffect, use } from 'react';
import { Link, useParams, useNavigate } from 'react-router-dom';
import './Section_menu.css'
import Statusbox from './Statusbox'

function Menu({ status }) {
    useEffect(() => {
        axios.get(`http://localhost:3000/api/tango/result/${status.section}`)
          .then(response => setResult(response.data))
        console.log(resultNum);
      }, [])

    let params = useParams();
    const section = params.sectionNum;
    const navigate = useNavigate()
    const move = (this_status) => {
        navigate('/quez/'+this_status.section)
    }



  return (

    <div>
        <div className="start">
            <Link className="start-hide" to={'/'}>←Home</Link>
            <h2 className="section-num">Section{section}</h2>
        </div>
        <div>
            <Statusbox
            text='進捗'
            percentage={status.answered}
            bar_color='blue'
            emp='3'
            />
            <Statusbox
                text='定着度'
                percentage={status.answerrate}
                bar_color='green'
                emp='3'
            />
        </div>


        <div className="start-btn box light-theme">
            {/* <Link to={'/quez/'+section}>始める</Link>  */}
            <div onClick={()=>move(status)}>始める</div> 
        </div>
        <div className="mistake-btn box light-theme">
            <a href="#">覚えてない</a> 
        </div>
        <div className="new-btn box light-theme">
            <a href="#">新しい</a> 
        </div>
    </div>
  )
}

export default Menu
