import React, { useState } from 'react'
import styled from 'styled-components'

const Quez = () => {
  const [words, setWords] = useState([
    {korean:'의사', meaning:'医者', pronouce:'ウィサ', learning:0, section:1},
    {korean:'위치', meaning:'位置', pronouce:'ウィチ', learning:0, section:1},
    {korean:'사랑', meaning:'愛', pronouce:'サラン', learning:0, section:1}
  ]) 

  return (
    <div>
      {words.map((word) => (
      <Card>
        <Section_num>{word.section}</Section_num>
        <Word><p>{word.korean}</p></Word>
        <Card_hidden>
          <p></p>
          <p></p>
        </Card_hidden>
        <Button_wrap>
          <Button>〇</Button>
          <Button>△</Button>
          <Button>✕</Button>
        </Button_wrap>

        <Showanswer>答えを見る</Showanswer>
      </Card>
      ))}

    </div>
  )
}

export default Quez

