import React, { useEffect } from 'react';
import axios from 'axios';
import { Container } from '../Theme/globalStyles';
import { Link, useParams, useNavigate } from 'react-router-dom';
// import './Section_menu.css'
import Statusbox from '../Statusbox'
import styled from 'styled-components';
import { ButtonWrap } from '../Theme/globalStyles';

function Menu({ status, setStatus }) {
    let params = useParams();
    const section = params.sectionNum;
    const navigate = useNavigate()
    useEffect(() => {
        axios.get(`http://localhost:3000/api/tango/result/${section}`)
            .then(response => {setStatus(response.data)})

      }, [])
    const move = () => {
        navigate('/quez/'+section)
    }
    const startOver = () => {
        axios.post(`http://localhost:3000/api/tango/clear/${section}/0/3`)
        navigate('/quez/'+section)
    }
    // const nextMenu = () => {
    //     navigate('/menu/'+section)
    // }     
    const nextMenu = () => {
        setStatus('')
        const next = Number(section)+1
        axios.get(`http://localhost:3000/api/tango/result/${next}`)
        .then(response => {setStatus(response.data)})
        navigate('/menu/'+next)

    }

    const beforeMenu = () => {
        setStatus('')
        const before = Number(section)+1
        axios.get(`http://localhost:3000/api/tango/result/${before}`)
        .then(response => {setStatus(response.data)})
        navigate('/menu/'+before)

    }

  return (
    <MenuWrap>
        <div>
            <Link to={'/'}>←Home</Link>
            <h2>Section{section}</h2>
        </div>
        <BoxesWrap>
            <S>
                <Statusbox
                    text='進捗'
                    percentage={status ? `${status.answered}` : '0' }
                    bar_color='blue'
                    emp='3'
                />
            </S>
            <S>
                <Statusbox
                    text='定着度'
                    percentage={status ? `${status.answerrate}` : '0' }
                    bar_color='green'
                    emp='3'
                />
            </S>
        </BoxesWrap>


        <StartBtn>
            <div onClick={()=>move()}>始める</div> 
        </StartBtn>
        <MistakeBtn>
            <div onClick={() => startOver()}>1からはじめる</div> 
        </MistakeBtn>
        {/* <div className="new-btn box light-theme">
            <a href="/menu/3">新しい</a> 
        </div> */}
        {section<18 && <Next onClick={() => nextMenu()}>Next</Next>}
        {section>1 && <Before onClick={() => beforeMenu()}>Before</Before>}
        {/* <Movemenu>
            <div onClick={nextMenu()}>Next</div>
        </Movemenu> */}
        <Section_wrap>
            <div>新しく</div>
            <p>start</p>
        </Section_wrap>
    </MenuWrap>
  )
}

const Section_wrap = styled.li`
    width: 100%;
    border: solid 2px ${(props)=>props.color};
    border-radius: 5px;
    margin-bottom: 30px;
    box-shadow: ${props => props.color ? props.color :'rgb(255, 216, 109)'}  6px 6px, black 6px 6px 0 2px;
    border-radius: 15px;
    padding: 10px;
    background-color: ${({theme})=>theme.content};
    cursor: pointer;
    &:hover{
        background-color: ${({ theme }) => theme.hover};
    }
`


const S = styled.div`
    margin-right: 20px;
`
const MenuWrap = styled.div`
  max-width: 600px;
  margin: 120px auto;
`
const BoxesWrap = styled.div`
    max-width: 400px;
    margin: auto;
    display: flex;
    margin-bottom: 20px;
`
const StartBtn = styled.div`
    cursor: pointer;
    line-height: 70px;
    border-radius: 15px;
    border: solid 2px black;
    box-shadow: 7px 7px rgb(255, 154, 108), 7px 7px 0 2px black;
    margin-bottom: 20px;
    text-align: center;
    font-size: 40px;
`
const MistakeBtn = styled.div`
    cursor: pointer;
    line-height: 70px;
    border-radius: 15px;
    border: solid 2px black;
    box-shadow: 7px 7px rgb(255, 154, 108), 7px 7px 0 2px black;
    margin-bottom: 20px;
    text-align: center;
    font-size: 40px;
`
const Next = styled.div`
`
const Before = styled.div``

export default Menu
